def retira_pontuacao(texto):
    """funcao que dada uma frase retorna uma onde todos os caracteres de pontuacao,tenham sido subsituidos str,str ->str"""
    return str.replace(str.replace(str.replace(str.replace(str.replace(str.replace(str.replace(Texto,"."," "),","," "),":"," "),";"," "),"-"," "),"!"," "),"?"," ")