def retira_pontuacao(pontu):
    if (pontu[len(pontu)-1]) == '.':
        return str.replace (pontu, ',', '.') and str.replace (pontu, '.', ' ') and str.replace (pontu, ',', ' ') and str.replace (pontu, '?', ' ') and str.replace (pontu, '!', ' ') and str.replace (pontu, '-', ' ') and str.replace (pontu, ':', ' ') and str.replace (pontu, ';', ' ') and str.replace (pontu, str(pontu[len(pontu)-1]), ' ')